import React, { Component } from 'react'

// import AddPriscript from './AddPriscript';
import DatePicker from 'react-datepicker';  
import { Link } from 'react-router-dom';
import DHeader from '../Doctor/DHeader';
import Footer from '../../Footer';
export default class ShowAppointment extends Component {

    
    state = {
        Appointment: [],
        Patient :[],
       requestOptions:[]

}
            

    docotor_id = JSON.parse(localStorage.getItem('docotor_id'))
   
    async componentDidMount() {
        const response =  fetch('/Appointment/showAppointment/' + this.docotor_id);

        const body = await response.json();
        const response1 =  fetch('/Patient/findPatient/' + response.patientId);
        body.patientName = response1
        this.setState({ Appointment: body });
       
        
    }
    
    getpatinetId=(id)=>{
        const response1 =  fetch('/Patient/findPatient/' + id);
        const body1 = response1.json();
        this.setState({ Patient: body1 });
        return `${response1.firstname}`
    }


    render() {
        return (
            <div>

                {/* <Header /> */}
                <DHeader/>   

                <br />
                <form>
                    <h1 style={{ textAlign: 'center', }}><u>Appointment List</u></h1>


                   
            
         
                
                    <div style={{ margin: 40 }}>
                        <table class="table table-default table-bordered table-responsive " style={{ border: "solid" }} >
                            <thead class="thead-default" >
                                <tr>
                                    <th>Serial No</th>
                                    <th> Date</th>
                                    {/* <th> Patient ID</th> */}

                                    <th>Details</th>
                                    {/* <th>Priscription</th> */}
                                </tr>
                            </thead>


                            {this.state.Appointment.map((app, i) =>


                                <tbody>
                                    <tr key={app.appointId}>
                                        <td>{app.appointId}</td>
                                        {/* <td>{this.getpatinetId(app.patientId)}</td> */}
                                        <td>{app.appointDate}</td>
                                        {/* <td>{app.appointId}</td> */}
                                       

                                        {/* <td>{this.findPatient(app.appointId)}</td> */}

                                        {/* <td><a name="" id="" class="btn btn-primary"  role="button" onClick="/sh">show</a>
                                        
                                        </td> */}
                                        <td>
                                            {/* <a name="" id="" class="btn btn-success" role="button" href={"/PatientDetails/" + app.patientID}>show Datails</a>
                                             */}
                                             
                                              
                                             <Link className='btn btn-dark'   to={"/PatientDetails/" +  app.patientId+"/"+app.appointId}>show details</Link>
                                            

                                        </td>
                                       


                                    </tr>

                                </tbody>

                            )}

                        </table>
                    </div>

                </form>
                <Footer/>
            </div>
        )
    }
}
