import React from 'react'
import { useState } from 'react';
import { useEffect } from 'react';
import DHeader from '../Doctor/DHeader';
import { Link } from 'react-router-dom';
import Footer from '../../Footer';

export default function ShowAppointment() {

    const [Patients, setPatients] = useState([]);
    const [Appointments, setAppointments] = useState([]);

  
   const  docotor_id = JSON.parse(localStorage.getItem('docotor_id'))

     useEffect(() => {

      const accountInfoReq =   fetch('/Appointment/showAppointment/' + docotor_id)
      .then(response => response.json())
      .then((response) => {
               
        setAppointments(response);

      }
      );


Appointments.map(app=>
  {
    fetch(`/Patient/getName/${app.patientId}`).then(data=>{
      setPatients["name"]=data
    })
  })


  }, []);


  return (
    <div>

    <p>Appointments -</p>
    <DHeader />

<br />
<form>
    <h1 style={{ textAlign: 'center', }}><u>Appointment List</u></h1>



{console.log(Appointments)}


    <div style={{ margin: 40 }}>
        <table class="table table-default table-bordered table-responsive " style={{ border: "solid" }} >
            <thead class="thead-default" >
                <tr>
                    <th>Serial No</th>
                    <th>Name of patient</th>
                    <th> Date</th>
                    {/* <th> Patient ID</th> */}
                    <th>Details</th>
                    <th>Name</th>
                    {/* <th>Priscription</th> */}
                </tr>
            </thead>
      
            {
                  Appointments.map(
                    (app) =>
                      <tbody>
                        <tr key={app.appointId}>
                          <td>{app.appointId}</td>
                          <td>{app.appointDate}</td>
                          <td>{app.prescriptionTitle}</td>
                          <td>{app.prescriptionDetails}</td>
                          <td>{Patients.patientName}</td>
      {console.log("PAtinets = "+Patients)}
        
                        </tr>
                      </tbody>

                  )
                }


        </table>
    </div>

</form>
<Footer />


    </div>
  )
}
