import React, { Component } from 'react'

import Footer from '../../Footer';
import Header from '../Header/Header';
import {jsPDF} from 'jspdf';
import html2canvas from "html2canvas";


export default class PatHistory extends Component {

  id = JSON.parse(localStorage.getItem('id'));



  state = {
    Appointment: [],
    doctorId:[]
  };

  async componentDidMount() {
    const response = await fetch('/Appointment/patHistory/' + this.id);
    const body = await response.json();
    this.setState({ Appointment: body });

    // this.state.doctorId = body.doctorId;

    
    
      // this.state.Appointment.map(
      //   (app) =>
      //   this.setState({doctorId:(app.doctorId)}),
      //   console.log(this.state.Appointment),
      //   // console.log(this.state.Appointment[0].doctorId),
      //   console.log(this.state.Appointment[this.state.Appointment.length-1].doctorId)
        
        
      // )
    
  }

   createPDF = async(e) => {
    e.preventDefault();

    const pdf = new jsPDF("portrait", "pt", "a4"); 
    const data =  html2canvas(document.querySelector("#pdf"));
   // const img = data.toDataURL("image/png");
    //const imgProperties = pdf.getImageProperties(img);
    const pdfWidth = pdf.internal.pageSize.getWidth();
    const pdfHeight = (imgProperties.height * pdfWidth) / imgProperties.width;
    pdf.addImage(img, "PNG", 0, 0, pdfWidth, pdfHeight);
    pdf.save("shipping_label.pdf");
  };


  render() {
    return (
      <div>

        <div>

          <Header />
          <br />
          <form>
          {/* {console.log(this.state.Appointment[0].doctorId)}

            {console.log(this.state.Appointment[this.state.Appointment.length].doctorId)} */}

            <h1 style={{ textAlign: 'center', }}><u>History List</u></h1>

            <div style={{ margin: 40 }}>
              <table  class="table table-default table-bordered table-responsive " style={{ border: "solid" }} >
                <thead class="thead-default" id="pdf" >
                  <tr>
                    <th>ID</th>
                    <th> Date</th>

                    <th> Doctor ID </th>
                    <th>Title</th>
                    <th>detail</th>
                  </tr>
                </thead>


                {this.state.Appointment.map((app, i=1) =>


                  <tbody>
                    <tr key={app.appointId}>
                      <td>{++i}</td>
                      <td>{app.appointDate}</td>

                      <td>{app.doctorId}</td>
                      <td>{app.prescriptionTitle}</td>
                      <td>{app.prescriptionDetails}</td>
                      <td><button className='btn btn-dark' onClick={this.createPDF}>Download</button></td>


                    </tr>

                  </tbody>

                )}

              </table>
            </div>

          </form>


        </div>
        <Footer/>
      </div>



    )
  }

}
