import React from 'react'
import { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';


export default function ShowAppointment() {

    const [Patients, setPatients] = useState([]);
    const [Appointments, setAppointments] = useState([]);

    const [Appointment,setAppointment] = useState([]);
   const  docotor_id = JSON.parse(localStorage.getItem('docotor_id'))
    useEffect(() => {
        fetch('/Appointment/findApp/' + docotor_id)
            .then((response) => {
                // localStorage.setItem("docotor_id", JSON.stringify(response['docotor_id']));
                console.log(response)
                
               
            }, error => {
                console.log(error);

            }

            );
    }, []);

  return (
    <div>

    <p>Appointments -</p>

    <form>
                    <h1 style={{ textAlign: 'center', }}><u>Appointment List</u></h1>






                    <div style={{ margin: 40 }}>
                        <table class="table table-default table-bordered table-responsive " style={{ border: "solid" }} >
                            <thead class="thead-default" >
                                <tr>
                                    <th>Serial No</th>
                                    <th>Name of patient</th>
                                    <th> Date</th>
                                    {/* <th> Patient ID</th> */}
                                    <th>Details</th>
                                    {/* <th>Priscription</th> */}
                                </tr>
                            </thead>


                            {this.state.Appointment.map((app, i) =>
                                <tbody>
                                    <tr key={app.appointId}>
                                        <td>{app.appointId}</td>
                                        <td>
                                            {app.patientId}

                                        </td>
                                        <td>{app.appointDate}</td>
                                        {/* <td>{app.appointId}</td> */}


                                        {/* <td>{this.findPatient(app.appointId)}</td> */}

                                        {/* <td><a name="" id="" class="btn btn-primary"  role="button" onClick="/sh">show</a>
                                        
                                        </td> */}
                                        <td>
                                            {/* <a name="" id="" class="btn btn-success" role="button" href={"/PatientDetails/" + app.patientID}>show Datails</a>
                                             */}


                                            <Link className='btn btn-dark' to={"/PatientDetails/" + app.patientId + "/" + app.appointId}>show details</Link>


                                        </td>



                                    </tr>

                                </tbody>

                            )}

                        </table>
                    </div>

                </form>

    </div>
  )
}
