import React, { Component } from 'react'

import { Link } from 'react-router-dom';
import DHeader from '../Doctor/DHeader';
import Footer from '../../Footer';
export default class ShowAppointment extends Component {

    
    state = {
        Appointment: [],
        Patient :[],
       requestOptions:[]

}
            

    docotor_id = JSON.parse(localStorage.getItem('docotor_id'))
   
    async componentDidMount() {
        const response = await fetch('/Appointment/showAppointment/' + this.docotor_id);
        const body = await response.json();
        this.setState({ Appointment: body });
       
        const response1 = await fetch('/Patient/findPatient/' + this.Appointment.patientId);
        const body1 = await response1.json();
        this.setState({ Patient: body1 });
    }
    


    render() {
        return (
            <div>

                {/* <Header /> */}
                <DHeader/>   

                <br />
                <form>
                    <h1 style={{ textAlign: 'center', }}><u>Appointment List</u></h1>


                   
            
         
                
                    <div style={{ margin: 40 }}>
                        <table class="table table-default table-bordered table-responsive " style={{ border: "solid" }} >
                            <thead class="thead-default" >
                                <tr>
                                    <th>Serial No</th>
                                    <th>Name of patient</th>
                                    <th> Date</th>
                                    {/* <th> Patient ID</th> */}
                                    <th>Details</th>
                                    {/* <th>Priscription</th> */}
                                </tr>
                            </thead>


                            {this.state.Appointment.map((app, i) =>
                                <tbody>
                                    <tr key={app.appointId}>
                                        <td>{app.appointId}</td>
                                        <td>
                                       this.Patient=  fetch("/Patient/findPatient/"+`${app.patientId}`);
                                       this.app.patname = this.Patient.patientname
                                      {app.patname}
                                           

                                        </td>
                                        <td>{app.appointDate}</td>
                                        {/* <td>{app.appointId}</td> */}
                                       

                                        {/* <td>{this.findPatient(app.appointId)}</td> */}

                                        {/* <td><a name="" id="" class="btn btn-primary"  role="button" onClick="/sh">show</a>
                                        
                                        </td> */}
                                        <td>
                                            {/* <a name="" id="" class="btn btn-success" role="button" href={"/PatientDetails/" + app.patientID}>show Datails</a>
                                             */}
                                             
                                              
                                             <Link className='btn btn-dark'   to={"/PatientDetails/" +  app.patientId+"/"+app.appointId}>show details</Link>
                                            

                                        </td>
                                       


                                    </tr>

                                </tbody>

                            )}

                        </table>
                    </div>

                </form>
                <Footer/>
            </div>
        )
    }
}
