import React from 'react'
import { useState } from 'react';
import { useEffect } from 'react';
import DHeader from '../Doctor/DHeader';

export default function ShowAppointment() {

    const [Patients, setPatients] = useState([]);
    const [Appointments, setAppointments] = useState({
      appointId : '',
      appointDate: '',
      patientId: '',
      doctorId: '',
      paymentId: '',
      prescriptionTitle: '',
      prescriptionDetails: '',
      status : false
}
);

  
   const  docotor_id = JSON.parse(localStorage.getItem('docotor_id'))

     useEffect(() => {

    //     fetch('/Appointment/showAppointment/1')
    //         .then((response) => {
    //             setAppointments(response)
    //             response.json()
    //         })
    //         .then((data)=> console.log(data))
    //         .catch((error) => {
    //             console.log(error);
    //         })
            

            
    // }, []);
    fetch('/Appointment/showAppointment/' + docotor_id)
      .then(response => response.json())
      .then((response) => {
        // localStorage.setItem("docotor_id", JSON.stringify(response['docotor_id']));
        console.log(response)
        setAppointments(response);
          
      }, error => {

        console.log(error);

      }
      );

      // fetch('/Patient/findPatient/' + Appointments.patientId)
      // .then(response => response.json())
      // .then((response) => {
      //   // localStorage.setItem("docotor_id", JSON.stringify(response['docotor_id']));
      //   console.log(response)
      //   setPatients(response);

      // }, error => {

      //   console.log(error);

      // }
      // );



  }, []);


  return (
    <div>

    <p>Appointments -</p>
    <DHeader />

<br />
<form>
    <h1 style={{ textAlign: 'center', }}><u>Appointment List</u></h1>






    <div style={{ margin: 40 }}>
        <table class="table table-default table-bordered table-responsive " style={{ border: "solid" }} >
            <thead class="thead-default" >
                <tr>
                    <th>Serial No</th>
                    <th>Name of patient</th>
                    <th> Date</th>
                    {/* <th> Patient ID</th> */}
                    <th>Details</th>
                    {/* <th>Priscription</th> */}
                </tr>
            </thead>


            {this.state.Appointment.map((app, i) =>
                <tbody>
                    <tr key={app.appointId}>
                        <td>{app.appointId}</td>
                        <td>
                            {app.patientId}

                        </td>
                        <td>{app.appointDate}</td>
                        {/* <td>{app.appointId}</td> */}


                        {/* <td>{this.findPatient(app.appointId)}</td> */}

                        {/* <td><a name="" id="" class="btn btn-primary"  role="button" onClick="/sh">show</a>
                        
                        </td> */}
                        <td>
                            {/* <a name="" id="" class="btn btn-success" role="button" href={"/PatientDetails/" + app.patientID}>show Datails</a>
                             */}


                            <Link className='btn btn-dark' to={"/PatientDetails/" + app.patientId + "/" + app.appointId}>show details</Link>


                        </td>



                    </tr>

                </tbody>

            )}

        </table>
    </div>

</form>
<Footer />


    </div>
  )
}
